import React, {Component} from "react";
import './Help.css';

class TextCard extends Component {

  render (){

    const text = [
      "Take a soil sample from your field.",
      "Mix the soil sample with the buffer in the kit.",
      "Tap your phone on the tag.",
      "Upload your results to the website!"
    ];

    const numb = this.props.number;

    return(
      <div class="card-body card-step">
        <p class="card-text card-right">{text[numb]}</p>
      </div>
    );

  }
}

export default TextCard;
