import React, {Component} from "react";
import './Help.css';

class TextCard extends Component {

  render (){

    const text = [
      "Divide your field into grids using the field map tool. Start sampling at grid 1,1. /n" +
      "Remove large debris at sample size. Using a soil core sampler or a spade, dig approximately 20m down. Obtain sufficient soil to mic in a flask.</p>" +
      "<p>Continue to systematically sample at each grid site and form composites. Approximately 5 sites can form 1 composite. Fill up to soil maximum fill-line.</p>",
      "Mix the soil sample with the buffer in the kit.",
      "Tap your phone on the tag.",
      "Upload your results to the website!"
    ];

    const numb = this.props.number;

    return(
      <div class="card-body card-step">
        <p class="card-text card-right">{text[numb]}</p>
      </div>
    );

  }
}

export default TextCard;
